import styled from "styled-components";

export const Nav = styled.nav`
  width: 100%;
  height: 90px;
  padding: 0 20px;
  display: flex;
  align-items: center;
  position: fixed;
  justify-content: space-between;
  // above all elements
  /* z-index: 98; */
  background: rgba(102, 51, 153, 0.8);
  // animate and move the Nav
  transition: all 2s ease-in-out;
`;

export const Menu = styled.div`
  cursor: pointer;
  background: ${({ theme }) => theme.colors.lightNavyBlue};
  padding: 0.7rem;
  border-radius: 10px;
  // only display on mobilephone
  display: none;

  @media (max-width: ${({ theme }) => theme.mobile}) {
    display: inline-block;
  }
`;

export const LineOne = styled.div`
  width: 15px;
  height: 3px;
  margin: 5px 0;
  background: rgba(240, 247, 255, 1);
  /* linear-gradient(135deg, rgba(240, 110, 15, 1) 0%); */
`;
export const LineTwo = styled.div`
  width: 30px;
  height: 3px;
  margin: 5px 0;
  background: rgba(240, 247, 255, 1);
  /* linear-gradient(135deg, rgba(240, 110, 15, 1) 0%); */
`;
export const LineThree = styled.div`
  width: 10px;
  height: 3px;
  margin: 5px 0;
  margin-left: 15px;
  background: rgba(240, 247, 255, 1);
  /* linear-gradient(135deg, rgba(240, 110, 15, 1) 0%); */
`;

export const NavList = styled.ul`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  font-size: 16px;
  flex-wrap: wrap;

  @media (max-width: ${({ theme }) => theme.mobile}) {
    width: 24rem;
    height: 100vh;
    background: ${({ theme }) => theme.colors.purple};
  }
`;

export const NavBar = () => {
  return (
    <>
      <Nav>
        {/* <div>
          <a href="#logo section"></a>
        </div> */}

        <Menu>
          <LineOne />
          <LineTwo />
          <LineThree />
        </Menu>

        <NavList>
          <li>About</li>
          <li>Services</li>
        </NavList>
      </Nav>
    </>
  );
};
