import styled from "styled-components";

export const StyledProjectSection = styled.section`
  width: 100%;
  background: linear-gradient(
      150deg,
      rgba(0, 64, 77, 0.9) 0%,
      rgba(2, 12, 27, 0.8) 100%
    ),
    url("./images/background.jpeg") center no-repeat;
  background-size: 100% 100%;
  min-height: 100vh;
  padding: 10rem 0 5rem 0;
`;

export const SingleProject = styled.div`
  display: flex;
  align-items: center;

  &:nth-child(even) {
    flex-direction: row-reverse;
    margin: 10rem 0;
  }
`;

export const ProjectImageContainer = styled.a`
  display: flex;
  flex: 1;
  position: relative;
  align-items: center;
  justify-content: center;
  /* rotate the project images */
  perspective: 1000px;

  img {
    width: 90%;
    border-radius: 20px;
    transform: rotateY(50deg);
    transition: ${({ theme }) => theme.transition};
    border-radius: 10%;
    border: 8px double rgba(147, 112, 219, 0.8);
  }
  /* rotating project images */
  &:hover {
    img {
      transform: rotateY(20deg);
    }
  }

  @media (max-width: ${({ theme }) => theme.mobile}) {
    margin-top: 3rem;
    justify-content: flex-start;
    img {
      transform: rotateY(0deg);
      width: 80%;
      border-radius: 9px;
    }
  }
`;

export const ProjectDescription = styled.div`
  flex: 1;

  h1 {
    font-size: 4rem;
    margin: 0 5rem 2rem 0;

    color: rgba(147, 112, 219, 0.8);
    /* background: rgb(102, 51, 153);
    border-radius: 10%; */
    text-shadow: 2px 2px;
    /* background-clip: text; */
    transform: rotateY(50deg);

    /* transition: ${({ theme }) => theme.transition}; */
  }
  &:hover {
    h1 {
      transform: rotateY(10deg);
    }
  }
`;

export const Tags = styled.div``;
