import styled from "styled-components";
import { useState } from "react";
import { NavData } from "../../data/NavData";

const Nav = styled.nav`
  width: 100%;
  height: 80px;
  padding: 0 20px;
  display: flex;
  align-items: center;
  position: fixed;
  justify-content: space-between;
  // above all elements
  z-index: 98;
  background: rgba(147, 112, 219, 0.8);
  // animate and move the Nav
  transition: all 2s ease-in-out;
`;

const Menu = styled.div`
  cursor: pointer;
  background: ${({ theme, menuOpen }) =>
    menuOpen ? "none" : theme.colors.lightNavyBlue};
  transition: ${({ theme }) => theme.transition};
  padding: 0.7rem;
  border-radius: 5px;
  display: none;
  position: absolute;
  right: 20px;
  z-index: 999;

  @media (max-width: ${({ theme }) => theme.mobile}) {
    display: inline-block;
  }
`;
export const Logo = styled.img`
  width: 50px;
  border-radius: 50%;
`;
export const LineOne = styled.div`
  width: 10px;
  height: 3px;
  margin: 5px 0;
  margin-left: 15px;
  background: linear-gradient(
    135deg,
    rgba(102, 51, 153, 1) 0,
    rgba(240, 247, 255, 1) 100%
  );
  transition: ${({ theme }) => theme.transition};
  transform: ${({ menuOpen }) =>
    menuOpen ? "rotate(-45deg) translate(-5px, 6px) scaleX(2)" : "none"};
`;
export const LineTwo = styled.div`
  width: 30px;
  height: 3px;
  margin: 5px 0;
  background: linear-gradient(
    135deg,
    rgba(102, 51, 153, 1) 0,
    rgba(240, 247, 255, 1) 100%
  );
  opacity: ${({ menuOpen }) => (menuOpen ? 0 : 1)};
  transition: ${({ theme }) => theme.transition};
`;
export const LineThree = styled.div`
  width: 10px;
  height: 3px;
  margin: 5px 0;
  margin-left: 15px;
  background: linear-gradient(
    135deg,
    rgba(102, 51, 153, 1) 0,
    rgba(240, 247, 255, 1) 100%
  );
  transition: ${({ theme }) => theme.transition};
  transform: ${({ menuOpen }) =>
    menuOpen ? "rotate(+45deg) translate(-5px, 6px) scaleX(3)" : "none"};
`;

export const NavList = styled.ul`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  font-size: 16px;
  flex-wrap: wrap;

  @media (max-width: ${({ theme }) => theme.mobile}) {
    width: 24rem;
    height: 100vh;
    background: ${({ theme }) => theme.colors.purple};
    position: fixed;
    top: 0;
    right: ${({ menuOpen }) => (menuOpen ? "0" : "-24rem")};
    padding-top: 3rem;
    justify-content: flex-start;
    flex-direction: column;
    z-index: 998;
    transition: ${({ theme }) => theme.transition};
  }
`;

export const NadData = styled.li`
  margin: 0.2rem 0.7rem;
`;
export const NavBar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  return (
    <div>
      <Nav>
        <div>
          <a href="#logo section">
            <Logo src="./" />
          </a>
        </div>

        <Menu onClick={() => setMenuOpen(!menuOpen)}>
          <LineOne menuOpen={menuOpen} />
          <LineTwo menuOpen={menuOpen} />
          <LineThree menuOpen={menuOpen} />
        </Menu>
        <NavList menuOpen={menuOpen}></NavList>
        {NavData &&
          NavData.map((navData, index) => (
            <li>
              <a href="">About</a>
            </li>
          ))}
      </Nav>
    </div>
  );
};
